mapper.select:

select 
	rn, bno, tag, title, content, writer, views, writedate
from
(SELECT 
	/*+ index_desc(pro_board pk_pro_board) */ 
	rownum rn, bno, tag, title, content, writer, views, writedate
from pro_board where rownum <= #{maxRow}
and tag like 'notice'
) where rn > #{minRow}


select 
	rn, bno, tag, title, content, writer, views, writedate
from
(SELECT 
	/*+ index_desc(pro_board pk_pro_board) */ 
	rownum rn, bno, tag, title, content, writer, views, writedate
from pro_board where rownum <= #{maxRow}
and tag like 'ask'
) where rn > #{minRow}

mapper.update:
  UPDATE pro_board
	SET views = views + 1
  WHERE bno=#{bno}
